(206, 'Module 6: Reading and Writing Local Data', '<p>Lessons</p>\r\n\r\n<ul>\r\n	<li>Reading and Writing Files</li>\r\n	<li>Serializing and Deserializing Data</li>\r\n	<li>Performing I/O Using Streams</li>\r\n</ul>\r\n\r\n<p>Lab : Generating the Grades Report</p>\r\n\r\n<ul>\r\n	<li>Serializing the Data for the Grades Report as XML</li>\r\n	<li>Previewing the Grades Report</li>\r\n	<li>Persisting the Serialized Grades Data to a File</li>\r\n</ul>\r\n', 29, '2018-01-18', '0000-00-00'),
(207, 'Module 7: Accessing a Database', '<p>Lessons</p>\r\n\r\n<ul>\r\n	<li>Creating and Using Entity Data Models</li>\r\n	<li>Querying Data by Using LINQ</li>\r\n	<li>Updating Data by Using LINQ</li>\r\n</ul>\r\n\r\n<p>Lab : Retrieving and Modifying Grade Data</p>\r\n\r\n<ul>\r\n	<li>Creating an Entity Model from the The School of Fine Arts Database</li>\r\n	<li>Updating Student and Grade Data Using the Entity Framework</li>\r\n	<li>Extending the Entity Model to Validate Data</li>\r\n</ul>\r\n', 29, '2018-01-18', '0000-00-00'),
(208, 'Module 8: Accessing Remote Data', '<p>Lessons</p>\r\n\r\n<ul>\r\n	<li>Accessing Data Across the Web</li>\r\n	<li>Accessing Data in the Cloud</li>\r\n</ul>\r\n\r\n<p>Lab : Retrieving and Modifying Grade Data in the Cloud</p>\r\n\r\n<ul>\r\n	<li>Creating a WCF Data Service for the SchoolGrades Database</li>\r\n	<li>Integrating the WCF Data Service into the Application</li>\r\n	<li>Retrieving Student Photographs Over the Web (if time permits)</li>\r\n</ul>\r\n', 29, '2018-01-18', '0000-00-00'),
(209, 'Module 9: Designing the User Interface for a Graphical Application', '<p>Lessons</p>\r\n\r\n<ul>\r\n	<li>Using XAML to Design a User Interface</li>\r\n	<li>Binding Controls to Data</li>\r\n	<li>Styling a User Interface</li>\r\n</ul>\r\n\r\n<p>Lab : Customizing Student Photographs and Styling the Application</p>\r\n\r\n<ul>\r\n	<li>Customizing the Appearance of Student Photographs</li>\r\n	<li>Styling the Logon View</li>\r\n	<li>Animating the StudentPhoto Control (If Time Permits)</li>\r\n</ul>\r\n', 29, '2018-01-18', '0000-00-00'),
(210, 'Module 10: Improving Application Performance and Responsiveness', '<p>Lessons</p>\r\n\r\n<ul>\r\n	<li>Implementing Multitasking by using Tasks and Lambda Expressions</li>\r\n	<li>Performing Operations Asynchronously</li>\r\n	<li>Synchronizing Concurrent Access to Data</li>\r\n</ul>\r\n\r\n<p>Lab : Improving the Responsiveness and Performance of the Application</p>\r\n\r\n<ul>\r\n	<li>Ensuring that the User Interface Remains Responsive When Retrieving Data for Teachers</li>\r\n	<li>Providing Visual Feedback During Long-Running Operations</li>\r\n</ul>\r\n', 29, '2018-01-18', '0000-00-00'),
(211, 'Module 11: Integrating with Unmanaged Code', '<p>Lessons</p>\r\n\r\n<ul>\r\n	<li>Creating and Using Dynamic Objects</li>\r\n	<li>Managing the Lifetime of Objects and Controlling Unmanaged Resources</li>\r\n</ul>\r\n\r\n<p>Lab : Upgrading the Grades Report</p>\r\n\r\n<ul>\r\n	<li>Generating the Grades Report by Using Microsoft Office Word</li>\r\n	<li>Controlling the Lifetime of Word Objects by Implementing the Dispose Pattern</li>\r\n</ul>\r\n', 29, '2018-01-18', '0000-00-00'),
