INSERT INTO `laba_course_outline` (`id`, `title`, `content`, `course_id`, `created`, `updated`) VALUES
(311, '2. Getting Started with Java', '<p>Writing a Simple Class</p>\r\n\r\n<ul>\r\n	<li>Classes in Java</li>\r\n	<li>Class Modifiers and Types</li>\r\n	<li>Class Instance Variables</li>\r\n	<li>Primitives vs. Object References</li>\r\n	<li>Creating Objects</li>\r\n</ul>\r\n\r\n<p>Adding Methods to the Class</p>\r\n\r\n<ul>\r\n	<li>Passing Parameters Into Methods</li>\r\n	<li>Returning a Value From a Method</li>\r\n	<li>Overloaded Methods</li>\r\n	<li>Constructors</li>\r\n	<li>Optimizing Constructor Usage</li>\r\n</ul>\r\n\r\n<p>Language Statements</p>\r\n\r\n<ul>\r\n	<li>Operators</li>\r\n	<li>Comparison and Logical Operators</li>\r\n	<li>Looping</li>\r\n	<li>Continue and Break Statements</li>\r\n	<li>The switch Statement</li>\r\n	<li>The for-each() Loop</li>\r\n</ul>\r\n\r\n<p>Using Strings</p>\r\n\r\n<ul>\r\n	<li>Strings</li>\r\n	<li>String Methods</li>\r\n	<li>String Equality</li>\r\n	<li>String BufferString Builder</li>\r\n</ul>\r\n\r\n<p>Specializing in a Subclass</p>\r\n\r\n<ul>\r\n	<li>Extending a Class</li>\r\n	<li>Casting</li>\r\n	<li>The Object Class</li>\r\n	<li>Default Constructor</li>\r\n	<li>Implicit Constructor Chaining</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 37, '2018-01-19', '0000-00-00'),
(312, '3. Essential Java Programming', '<p>Fields and Variables</p>\r\n\r\n<ul>\r\n	<li>Instance vs. Local Variables: Usage Differences</li>\r\n	<li>Data Types</li>\r\n	<li>Default Values</li>\r\n	<li>Block Scoping Rules</li>\r\n	<li>Final and Static Fields</li>\r\n	<li>Static Methods</li>\r\n</ul>\r\n\r\n<p>Using Arrays</p>\r\n\r\n<ul>\r\n	<li>Arrays</li>\r\n	<li>Accessing the Array</li>\r\n	<li>Multidimensional Arrays</li>\r\n	<li>Copying Arrays</li>\r\n	<li>Variable Arguments</li>\r\n</ul>\r\n\r\n<p>Java Packages and Visibility</p>\r\n\r\n<ul>\r\n	<li>Class Location of Packages</li>\r\n	<li>The Package Keyword</li>\r\n	<li>Importing Classes</li>\r\n	<li>Executing Programs</li>\r\n	<li>Java Naming Conventions</li>\r\n</ul>\r\n', 37, '2018-01-19', '0000-00-00'),
